'use strict';

var base = require('@web3auth/base');
var react = require('react');
var Web3AuthInnerContext = require('../context/Web3AuthInnerContext.js');

const useWeb3Auth = () => {
  const context = react.useContext(Web3AuthInnerContext.Web3AuthInnerContext);
  if (!context) {
    throw base.WalletInitializationError.fromCode(1000, "usage of useWeb3Auth not wrapped in `Web3AuthContextProvider`.");
  }
  return context;
};

exports.useWeb3Auth = useWeb3Auth;
